00012 bool VL53L0X_getSpadInfo(I2C_dev_handles device_handle ,uint8_t * count, bool * type_is_aperture)
00013 {
00014   uint8_t tmp;
00015 
00016   I2C_writeRegister8bit(device_handle, 0x80, 0x01);
00017   I2C_writeRegister8bit(device_handle, 0xFF, 0x01);
00018   I2C_writeRegister8bit(device_handle, 0x00, 0x00);
00019   I2C_writeRegister8bit(device_handle, 0xFF, 0x06);
00020   I2C_writeRegister8bit(device_handle, 0x83, (uint8_t)(I2C_readRegister16bit(device_handle, 0x83)>>8) | 0x04);
00021   I2C_writeRegister8bit(device_handle, 0xFF, 0x07);
00022   I2C_writeRegister8bit(device_handle, 0x81, 0x01);
00023   I2C_writeRegister8bit(device_handle, 0x80, 0x01);
00024   I2C_writeRegister8bit(device_handle, 0x94, 0x6b);
00025   I2C_writeRegister8bit(device_handle, 0x83, 0x00);
00026   uint8_t reg;
00027   startTimeout();
00028   do
00029   {
00030     reg = (uint8_t)(I2C_readRegister16bit(device_handle, 0x83)>>8);
00031     if (checkTimeoutExpired()) { return false; }
00032     vTaskDelay(pdMS_TO_TICKS(10));
00033   } while(reg == 0x00);
00034   I2C_writeRegister8bit(device_handle, 0x83, 0x01);
00035   tmp = (uint8_t)(I2C_readRegister16bit(device_handle, 0x92)>>8);
00036 
00037   *count = tmp & 0x7f;
00038   *type_is_aperture = (tmp >> 7) & 0x01;
00039 
00040   I2C_writeRegister8bit(device_handle, 0x81, 0x00);
00041   I2C_writeRegister8bit(device_handle, 0xFF, 0x06);
00042 //  uint8_t data;
00043   I2C_writeRegister8bit(device_handle, 0x83, (uint8_t)(I2C_readRegister16bit(device_handle, 0x83)>>8) & ~0x04);
00044   I2C_writeRegister8bit(device_handle, 0xFF, 0x01);
00045   I2C_writeRegister8bit(device_handle, 0x00, 0x01);
00046 
00047   I2C_writeRegister8bit(device_handle, 0xFF, 0x00);
00048   I2C_writeRegister8bit(device_handle, 0x80, 0x00);
00049 
00050   return true;
00051 }
