00058 static type_error VL53L0X_setSignalRateLimit (I2C_dev_handles device_handle, float limit_Mcps)
00059 {
00060    if (limit_Mcps < 0 || limit_Mcps > 511.99)
00061       return err;
00062    // Q9.7 fixed point format (9 integer bits, 7 fractional bits)
00063    I2C_writeRegister16bit(device_handle, FINAL_RANGE_CONFIG_MIN_COUNT_RATE_RTN_LIMIT, limit_Mcps * (1 << 7));
00064    return not_err;
00065 }
