00067 bool VL53L0X_Init(I2C_dev_handles device_handle)
00068 {
00069       if((uint8_t)(I2C_readRegister16bit(device_handle, IDENTIFICATION_MODEL_ID)>>8)!=0xEE)
00070         return false;
00071 
00072       uint16_t data;
00073 
00074      // "Set I2C standard mode"
00075       I2C_writeRegister8bit(device_handle,0x88, 0x00);
00076       I2C_writeRegister8bit(device_handle,0x80, 0x01);
00077       I2C_writeRegister8bit(device_handle,0xFF, 0x01);
00078       I2C_writeRegister8bit(device_handle,0x00, 0x00);
00079 
00080       stop_variable = (uint8_t)(I2C_readRegister16bit(device_handle, 0x91)>>8);
00081       ESP_LOGI(" ", "stop_variable: %u", stop_variable);
00082       I2C_writeRegister8bit(device_handle,0x00, 0x01);
00083       I2C_writeRegister8bit(device_handle,0xFF, 0x00);
00084       I2C_writeRegister8bit(device_handle,0x80, 0x00);
00085 
00086       // disable SIGNAL_RATE_MSRC (bit 1) and SIGNAL_RATE_PRE_RANGE (bit 4) limit checks
00087       uint8_t MSRC_CONFIG = (uint8_t)(I2C_readRegister16bit(device_handle, MSRC_CONFIG_CONTROL)>>8);
00088       I2C_writeRegister8bit(device_handle, MSRC_CONFIG_CONTROL, MSRC_CONFIG | 0x12) ;
00089 
00090 
00091 
00092       data = VL53L0X_setSignalRateLimit (device_handle, 0.25);
00093 
00094 
00095       I2C_writeRegister8bit(device_handle, SYSTEM_SEQUENCE_CONFIG, 0xFF);
00096 
00097       uint8_t spad_count;
00098       bool spad_type_is_aperture;
00099       if (!VL53L0X_getSpadInfo(device_handle ,&spad_count, &spad_type_is_aperture)) {
00100           //ESP_LOGI(" ","VL53L0X_getSpadInfo false");
00101           return false;
00102       }
00103       uint8_t ref_spad_map[6];
00104       uint8_t spad_enables_ref_0 = GLOBAL_CONFIG_SPAD_ENABLES_REF_0;
00105 
00106       I2C_transmit(device_handle, &spad_enables_ref_0, 1);
00107       vTaskDelay(pdMS_TO_TICKS(10));
00108       I2C_receive(device_handle, ref_spad_map, 6);
00109       vTaskDelay(pdMS_TO_TICKS(10));
00110 
00111       I2C_writeRegister8bit(device_handle, 0xFF, 0x01);
00112       I2C_writeRegister8bit(device_handle, DYNAMIC_SPAD_REF_EN_START_OFFSET, 0x00);
00113       I2C_writeRegister8bit(device_handle, DYNAMIC_SPAD_NUM_REQUESTED_REF_SPAD, 0x2C);
00114       I2C_writeRegister8bit(device_handle, 0xFF, 0x00);
00115       I2C_writeRegister8bit(device_handle, GLOBAL_CONFIG_REF_EN_START_SELECT, 0xB4);
00116 
00117       uint8_t first_spad_to_enable = spad_type_is_aperture ? 12 : 0; // 12 is the first aperture spad
00118       uint8_t spads_enabled = 0;
00119 
00120        for (uint8_t i = 0; i < 48; i++)
00121        {
00122          if (i < first_spad_to_enable || spads_enabled == spad_count)
00123          {
00124            // This bit is lower than the first one that should be enabled, or
00125            // (reference_spad_count) bits have already been enabled, so zero this bit
00126            ref_spad_map[i / 8] &= ~(1 << (i % 8));
00127          }
00128          else if ((ref_spad_map[i / 8] >> (i % 8)) & 0x1)
00129          {
00130            spads_enabled++;
00131          }
00132        }
00133        I2C_transmit(device_handle, &spad_enables_ref_0, 1);
00134        vTaskDelay(pdMS_TO_TICKS(10));
00135        I2C_transmit(device_handle, ref_spad_map, sizeof(ref_spad_map)); // 6
00136 
00137        VL53L0X_setupI2CRegisters(device_handle);
00138 
00139        I2C_writeRegister8bit(device_handle, SYSTEM_INTERRUPT_CONFIG_GPIO, 0x04);
00140        I2C_writeRegister8bit(device_handle, GPIO_HV_MUX_ACTIVE_HIGH, (uint8_t)(I2C_readRegister16bit(device_handle, GPIO_HV_MUX_ACTIVE_HIGH)>>8) & ~0x10); // active low
00141        I2C_writeRegister8bit(device_handle, SYSTEM_INTERRUPT_CLEAR, 0x01);
00142 
00143        measurement_timing_budget_us = VL53L0X_getMeasurementTimingBudget(device_handle);
00144        ESP_LOGI(" ","VL53L0X_setMeasurementTimingBudget %"PRIu32" \n", measurement_timing_budget_us);
00145        I2C_writeRegister8bit(device_handle, SYSTEM_SEQUENCE_CONFIG, 0xE8);
00146        if (!VL53L0X_setMeasurementTimingBudget(device_handle,measurement_timing_budget_us-10)) {
00147            //ESP_LOGI(" ","VL53L0X_setMeasurementTimingBudget false \n");
00148            return false;
00149        };
00150        I2C_writeRegister8bit(device_handle, SYSTEM_SEQUENCE_CONFIG, 0x01);
00151        if (!VL53L0X_performSingleRefCalibration(device_handle, 0x40)) {
00152            //ESP_LOGI(" ","VL53L0X_performSingleRefCalibration1 false \n");
00153            return false;
00154        }
00155        I2C_writeRegister8bit(device_handle, SYSTEM_SEQUENCE_CONFIG, 0x02);
00156        if (!VL53L0X_performSingleRefCalibration(device_handle, 0x00)) {
00157           // ESP_LOGI(" ","VL53L0X_performSingleRefCalibration2 false \n");
00158            return false;
00159        }
00160 
00161        I2C_writeRegister8bit(device_handle, SYSTEM_SEQUENCE_CONFIG, 0xE8);
00162 
00163        //ESP_LOGI(" ","VLX sensor init done! \n");
00164        return true;
