00167 uint32_t VL53L0X_getMeasurementTimingBudget(I2C_dev_handles device_handle)
00168 {
00169   SequenceStepEnables enables;
00170   SequenceStepTimeouts timeouts;
00171 
00172   uint16_t const StartOverhead     = 1910;
00173   uint16_t const EndOverhead        = 960;
00174   uint16_t const MsrcOverhead       = 660;
00175   uint16_t const TccOverhead        = 590;
00176   uint16_t const DssOverhead        = 690;
00177   uint16_t const PreRangeOverhead   = 660;
00178   uint16_t const FinalRangeOverhead = 550;
00179 
00180   // "Start and end overhead times always present"
00181   uint32_t budget_us = StartOverhead + EndOverhead;
00182 
00183   VL53L0X_getSequenceStepEnables(device_handle , &enables);
00184   VL53L0X_getSequenceStepTimeouts(device_handle, &enables, &timeouts);
00185 
00186   if (enables.tcc)
00187   {
00188     budget_us += (timeouts.msrc_dss_tcc_us + TccOverhead);
00189   }
00190 
00191   if (enables.dss)
00192   {
00193     budget_us += 2 * (timeouts.msrc_dss_tcc_us + DssOverhead);
00194   }
00195   else if (enables.msrc)
00196   {
00197     budget_us += (timeouts.msrc_dss_tcc_us + MsrcOverhead);
00198   }
00199 
00200   if (enables.pre_range)
00201   {
00202     budget_us += (timeouts.pre_range_us + PreRangeOverhead);
00203   }
00204 
00205   if (enables.final_range)
00206   {
00207     budget_us += (timeouts.final_range_us + FinalRangeOverhead);
00208   }
00209 
00210   measurement_timing_budget_us = budget_us; // store for internal reuse
00211   return budget_us;
00212 }
