00214 bool VL53L0X_setMeasurementTimingBudget(I2C_dev_handles device_handle, uint32_t budget_us)
00215 {
00216   SequenceStepEnables enables;
00217   SequenceStepTimeouts timeouts;
00218 
00219   uint16_t const StartOverhead     = 1910;
00220   uint16_t const EndOverhead        = 960;
00221   uint16_t const MsrcOverhead       = 660;
00222   uint16_t const TccOverhead        = 590;
00223   uint16_t const DssOverhead        = 690;
00224   uint16_t const PreRangeOverhead   = 660;
00225   uint16_t const FinalRangeOverhead = 550;
00226 
00227   uint32_t used_budget_us = StartOverhead + EndOverhead;
00228 
00229   VL53L0X_getSequenceStepEnables(device_handle,&enables);
00230   VL53L0X_getSequenceStepTimeouts(device_handle,&enables, &timeouts);
00231 
00232   if (enables.tcc)
00233   {
00234     used_budget_us += (timeouts.msrc_dss_tcc_us + TccOverhead);
00235   }
00236 
00237   if (enables.dss)
00238   {
00239     used_budget_us += 2 * (timeouts.msrc_dss_tcc_us + DssOverhead);
00240   }
00241   else if (enables.msrc)
00242   {
00243     used_budget_us += (timeouts.msrc_dss_tcc_us + MsrcOverhead);
00244   }
00245 
00246   if (enables.pre_range)
00247   {
00248     used_budget_us += (timeouts.pre_range_us + PreRangeOverhead);
00249   }
00250 
00251   if (enables.final_range)
00252   {
00253     used_budget_us += FinalRangeOverhead;
00254 
00255     // "Note that the final range timeout is determined by the timing
00256     // budget and the sum of all other timeouts within the sequence.
00257     // If there is no room for the final range timeout, then an error
00258     // will be set. Otherwise the remaining time will be applied to
00259     // the final range."
00260 
00261     if (used_budget_us > budget_us)
00262     {
00263       // "Requested timeout too big."
00264       return false;
00265     }
00266 
00267     uint32_t final_range_timeout_us = budget_us - used_budget_us;
00268     // (SequenceStepId == VL53L0X_SEQUENCESTEP_FINAL_RANGE)
00269 
00270     // "For the final range timeout, the pre-range timeout
00271     //  must be added. To do this both final and pre-range
00272     //  timeouts must be expressed in macro periods MClks
00273     //  because they have different vcsel periods."
00274     uint32_t final_range_timeout_mclks =
00275     VL53L0X_timeoutMicrosecondsToMclks(final_range_timeout_us,
00276                                 timeouts.final_range_vcsel_period_pclks);
00277 
00278     if (enables.pre_range)
00279     {
00280       final_range_timeout_mclks += timeouts.pre_range_mclks;
00281     }
00282 
00283     I2C_writeRegister8bit(device_handle, FINAL_RANGE_CONFIG_TIMEOUT_MACROP_HI,
00284     VL53L0X_encodeTimeout(final_range_timeout_mclks));
00285 
00286     // set_sequence_step_timeout() end
00287 
00288     measurement_timing_budget_us = budget_us; // store for internal reuse
00289   }
00290   return true;
00291 }
