00294 void VL53L0X_getSequenceStepEnables(I2C_dev_handles device_handle, SequenceStepEnables * enables)
00295 {
00296   uint8_t sequence_config = (uint8_t)(I2C_readRegister16bit(device_handle, SYSTEM_SEQUENCE_CONFIG)>>8);
00297 
00298   enables->tcc          = (sequence_config >> 4) & 0x1;
00299   enables->dss          = (sequence_config >> 3) & 0x1;
00300   enables->msrc         = (sequence_config >> 2) & 0x1;
00301   enables->pre_range    = (sequence_config >> 6) & 0x1;
00302   enables->final_range  = (sequence_config >> 7) & 0x1;
00303 }
