00305 void VL53L0X_getSequenceStepTimeouts(I2C_dev_handles device_handle, SequenceStepEnables const * enables, SequenceStepTimeouts * timeouts)
00306 {
00307   timeouts->pre_range_vcsel_period_pclks = VL53L0X_getVcselPulsePeriod(device_handle, VcselPeriodPreRange);
00308   timeouts->msrc_dss_tcc_mclks = (uint8_t)(I2C_readRegister16bit(device_handle, MSRC_CONFIG_TIMEOUT_MACROP)>>8) + 1;
00309   timeouts->msrc_dss_tcc_us = VL53L0X_timeoutMclksToMicroseconds(timeouts->msrc_dss_tcc_mclks,
00310                                timeouts->pre_range_vcsel_period_pclks);
00311   timeouts->pre_range_mclks = VL53L0X_decodeTimeout(I2C_readRegister16bit(device_handle, PRE_RANGE_CONFIG_TIMEOUT_MACROP_HI));
00312   timeouts->pre_range_us =VL53L0X_timeoutMclksToMicroseconds(timeouts->pre_range_mclks,
00313                                timeouts->pre_range_vcsel_period_pclks);
00314   timeouts->final_range_vcsel_period_pclks = VL53L0X_getVcselPulsePeriod(device_handle, VcselPeriodFinalRange);
00315   timeouts->final_range_mclks =
00316     VL53L0X_decodeTimeout(I2C_readRegister16bit(device_handle, FINAL_RANGE_CONFIG_TIMEOUT_MACROP_HI));
00317 
00318   if (enables->pre_range)
00319   {
00320     timeouts->final_range_mclks -= timeouts->pre_range_mclks;
00321   }
00322   timeouts->final_range_us =
00323     VL53L0X_timeoutMclksToMicroseconds(timeouts->final_range_mclks,
00324                                timeouts->final_range_vcsel_period_pclks);
00325 }
