00327 // Get the VCSEL pulse period in PCLKs for the given period type.
00328 // based on VL53L0X_get_vcsel_pulse_period()
00329 uint8_t VL53L0X_getVcselPulsePeriod(I2C_dev_handles device_handle, vcselPeriodType type)
00330 {
00331   if (type == VcselPeriodPreRange)
00332   {
00333     return decodeVcselPeriod((uint8_t)(I2C_readRegister16bit(device_handle, PRE_RANGE_CONFIG_VCSEL_PERIOD)>>8));
00334   }
00335   else if (type == VcselPeriodFinalRange)
00336   {
00337     return decodeVcselPeriod((uint8_t)(I2C_readRegister16bit(device_handle, FINAL_RANGE_CONFIG_VCSEL_PERIOD)>>8));
00338   }
00339   else { return 255; }
00340 }
