00342 uint32_t VL53L0X_timeoutMclksToMicroseconds(uint16_t timeout_period_mclks, uint8_t vcsel_period_pclks)
00343 {
00344   uint32_t macro_period_ns = calcMacroPeriod(vcsel_period_pclks);
00345 
