00349 uint16_t VL53L0X_decodeTimeout(uint16_t reg_val)
00350 {
00351   // format: "(LSByte * 2^MSByte) + 1"
00352   return (uint16_t)((reg_val & 0x00FF) <<
00353          (uint16_t)((reg_val & 0xFF00) >> 8)) + 1;
00354 }
00355 
00356 uint32_t VL53L0X_timeoutMicrosecondsToMclks(uint32_t timeout_period_us, uint8_t vcsel_period_pclks)
00357 {
00358   uint32_t macro_period_ns = calcMacroPeriod(vcsel_period_pclks);
00359   return (((timeout_period_us * 1000) + (macro_period_ns / 2)) / macro_period_ns);
00360 }
