00362 uint16_t VL53L0X_encodeTimeout(uint32_t timeout_mclks)
00363 {
00364   // format: "(LSByte * 2^MSByte) + 1"
00365 
00366   uint32_t ls_byte = 0;
00367   uint16_t ms_byte = 0;
00368 
00369   if (timeout_mclks > 0)
00370   {
00371     ls_byte = timeout_mclks - 1;
00372 
00373     while ((ls_byte & 0xFFFFFF00) > 0)
00374     {
00375       ls_byte >>= 1;
00376       ms_byte++;
00377     }
00378 
00379     return (ms_byte << 8) | (ls_byte & 0xFF);
00380   }
00381   else { return 0; }
00382 }
