00430 uint16_t VL53L0X_readRangeContinuousMillimeters(I2C_dev_handles device_handle)
00431 {
00432   startTimeout();
00433   uint16_t range = 0;
00434   static bool start_continuous_back2back_sens1 = false;
00435   static bool start_continuous_back2back_sens2 = false;
00436   if((start_continuous_back2back_sens1 == false && device_handle == I2C_distance_sens1_dev_handle) ||
00437      ((start_continuous_back2back_sens2 == false && device_handle == I2C_distance_sens2_dev_handle)))
00438   {
00439       I2C_writeRegister8bit(device_handle, 0x80, 0x01);
00440       I2C_writeRegister8bit(device_handle, 0xFF, 0x01);
00441       I2C_writeRegister8bit(device_handle, 0x00, 0x00);
00442       I2C_writeRegister8bit(device_handle, 0x91, stop_variable);
00443       I2C_writeRegister8bit(device_handle, 0x00, 0x01);
00444       I2C_writeRegister8bit(device_handle, 0xFF, 0x00);
00445       I2C_writeRegister8bit(device_handle, 0x80, 0x00);
00446       I2C_writeRegister8bit(device_handle, SYSRANGE_START, 0x02); // back to back mode = fast as possible
00447       if(device_handle == I2C_distance_sens1_dev_handle)
00448       start_continuous_back2back_sens1 = true;
00449       else start_continuous_back2back_sens2 = true;
00450       vTaskDelay(pdMS_TO_TICKS(20));
00451   }
00452 
00453   while (((uint8_t)(I2C_readRegister16bit(device_handle, RESULT_INTERRUPT_STATUS)>>8) & 0x07) == 0)
00454   {
00455     if (checkTimeoutExpired())
00456     {
00457       //did_timeout = true;
00458       return 0;
00459     }
00460   }
00461   // assumptions: Linearity Corrective Gain is 1000 (default);
00462   // fractional ranging is not enabled
00463   range = I2C_readRegister16bit(device_handle, RESULT_RANGE_STATUS + 10);
00464   I2C_writeRegister8bit(device_handle, SYSTEM_INTERRUPT_CLEAR, 0x01);
00465 
00466 
00467   return range;
00468 }
