00470 void VL53L0X_SetInterruptThresholds(I2C_dev_handles device_handle,
00471                                              uint32_t ThresholdLow,
00472                                              uint32_t ThresholdHigh) {
00473   uint16_t Threshold16;
00474 
00475   /* no dependency on DeviceMode for Ewok */
00476   /* Need to divide by 2 because the FW will apply a x2 */
00477   Threshold16 = (uint16_t)((ThresholdLow >> 17) & 0x00fff);
00478   I2C_writeRegister16bit(device_handle,  SYSTEM_THRESH_LOW, Threshold16);
00479 
00480   /* Need to divide by 2 because the FW will apply a x2 */
00481   Threshold16 = (uint16_t)((ThresholdHigh >> 17) & 0x00fff);
00482   I2C_writeRegister16bit(device_handle,  SYSTEM_THRESH_HIGH, Threshold16);
00483   //ESP_LOGI("", "Set treshold.");
00484 }
